<?php	$oper = isset($REQUEST['oper']) ? $REQUEST['oper'] : '';                if($oper=="pagetop" OR $oper=='')        {        ?><style>    .scrollup {    width: 40px;    height: 40px;    position: fixed;    bottom: 50px;    right: 100px;    display: none;    text-indent: -9999px;    background: url('img/page_top.png') no-repeat;    z-index:1000;}</style><script type="text/javascript" charset="utf-8">	    $(document).ready(function () {    $(window).scroll(function () {        if ($(this).scrollTop() > 100) {            $('.scrollup').fadeIn();        } else {            $('.scrollup').fadeIn();        }    });    $('.scrollup').click(function () {        $("html, body").animate({            scrollTop: 0        }, 600);        return false;    });});</script><a href="#" class="scrollup" title="Page Top" cursor="pointer">Scroll</a><?php } ?>